// Récupération des informations du fichier uploadé et validation basique
const documentId = 'doc-' + Date.now();
let errors = [];
let binaryData = null;
let fileName = 'document.pdf';
let fileSize = 0;
let binaryProperty = null;

// Vérifier si nous avons reçu un fichier
if ($input.item && $input.item.binary) {
  const keys = Object.keys($input.item.binary);
  if (keys.length > 0) {
    binaryProperty = keys[0]; // Sauvegarde de la clé binaire
    const fileData = $input.item.binary[keys[0]];
    if (fileData) {
      binaryData = fileData;
      fileName = fileData.fileName || 'document.pdf';
      fileSize = fileData.fileSize || 0;

      // Assurons-nous que le nom de fichier a l'extension .pdf
      if (!fileName.toLowerCase().endsWith('.pdf')) {
        fileName += '.pdf';
      }

      // Vérification de la taille maximale (200 Mo)
      const MAX_SIZE = 200 * 1024 * 1024;
      if (fileSize > MAX_SIZE) {
        errors.push(`Le fichier est trop volumineux (${Math.round(fileSize / (1024 * 1024))} Mo). Maximum autorisé: 200 Mo`);
      }
    } else {
      errors.push('Données de fichier invalides');
    }
  } else {
    errors.push('Aucune donnée binaire trouvée');
  }
} else {
  errors.push('Aucun fichier n\'a été téléchargé');
}

// Informations de base pour le débogage
console.log('Détails du fichier:', { 
  fileName, 
  fileSize: fileSize ? (fileSize / (1024 * 1024)).toFixed(2) + ' MB' : 'N/A' 
});

// Configuration globale avec les noms de conteneurs et ports corrects
const config = {
  // Utiliser directement /tmp qui existe déjà sur presque tous les systèmes
  storageFolder: '/tmp/',
  documentProcessorUrl: 'http://technicia-document-processor:8000/api/process',
  schemaAnalyzerUrl: 'http://technicia-vision-classifier:8000/api/analyze',
  vectorEngineUrl: 'http://technicia-vector-store:8000/api/vectorize',
  environment: 'production',
  version: '1.0.0'
};

// Créer le chemin complet du fichier - Plus simple
const uniqueFileName = documentId + '-' + fileName;
const fullPath = config.storageFolder + uniqueFileName;

// Récupérer les données binaires pour les passer au prochain nœud
const inputBinary = $input.item.binary || {};

// Résultat validé
return {
  json: {
    documentId,
    fileName,
    uniqueFileName,
    fileSize,
    mimeType: 'application/pdf', // On force le type MIME à PDF
    errors,
    isValid: errors.length === 0,
    timestamp: new Date().toISOString(),
    // Données binaires
    binaryProperty, // La variable est maintenant définie en dehors des conditions
    // Chemins
    storageFolder: config.storageFolder,
    fullPath,
    // URLs microservices
    documentProcessorUrl: config.documentProcessorUrl,
    schemaAnalyzerUrl: config.schemaAnalyzerUrl,
    vectorEngineUrl: config.vectorEngineUrl
  },
  // Passer les données binaires telles quelles
  binary: inputBinary
};

// Renommer les données binaires pour qu'elles soient accessibles sous le nom 'data'
const output = {};

// Copier toutes les propriétés JSON
output.json = {...$input.item.json};

// Préparer l'objet binaire
output.binary = {};

// Vérifier si des données binaires sont disponibles
if ($input.item.binary) {
  const binaryKeys = Object.keys($input.item.binary);

  if (binaryKeys.length > 0) {
    // Récupérer les données binaires de la première clé disponible
    const firstBinaryKey = binaryKeys[0];

    // Assigner ces données à la clé 'data' pour le nœud Write Binary File
    output.binary.data = $input.item.binary[firstBinaryKey];

    console.log(`Données binaires renommées: ${firstBinaryKey} -> data`);
  } else {
    console.log('Aucune donnée binaire disponible à renommer');
  }
}

return output;

// Préparation de l'analyse des schémas
// Récupérer les résultats du Document Processor
const docProcessorResults = $json.body || {};

// Vérifier si le Document Processor a bien fonctionné
if (!docProcessorResults.success) {
  throw new Error(`Document Processor failed: ${docProcessorResults.message || 'Unknown error'}`);
}

// Récupérer les données du document
const documentInfo = $input.item.json;

// Extraire les informations nécessaires
const documentId = documentInfo.documentId;
const extractedImages = docProcessorResults.images || [];
const storageFolder = documentInfo.storageFolder;

// Préparation des données pour Schema Analyzer
return {
  json: {
    // Paramètres du document
    documentId,
    storageFolder,
    // URLs microservices
    schemaAnalyzerUrl: documentInfo.schemaAnalyzerUrl,
    vectorEngineUrl: documentInfo.vectorEngineUrl,
    // Données extraites par Document Processor
    images: extractedImages.map(img => ({
      id: img.id,
      path: img.path,
      page: img.page,
      width: img.width,
      height: img.height
    })),
    textBlocks: docProcessorResults.textBlocks || [],
    pageCount: docProcessorResults.pageCount || 0,
    metadata: docProcessorResults.metadata || {},
    // Autres informations
    fileName: documentInfo.fileName,
    fullPath: documentInfo.fullPath,
    timestamp: new Date().toISOString()
  }
};

// Traitement des résultats de Schema Analyzer
const analyzerResults = $json.body || {};

// Vérifier si le Schema Analyzer a bien fonctionné
if (!analyzerResults.success) {
  throw new Error(`Schema Analyzer failed: ${analyzerResults.message || 'Unknown error'}`);
}

// Récupérer les données du document
const documentInfo = $input.item.json;

// Extraire les informations nécessaires
const documentId = documentInfo.documentId;
const textBlocks = documentInfo.textBlocks;
const pageCount = documentInfo.pageCount;
const metadata = documentInfo.metadata;
const processedImages = analyzerResults.images || [];

// Statistiques sur les images
const stats = {
  totalImages: processedImages.length,
  technicalDiagrams: processedImages.filter(img => img.classification === 'technical_diagram').length,
  decorativeImages: processedImages.filter(img => img.classification !== 'technical_diagram').length,
  imagesWithText: processedImages.filter(img => img.ocrText && img.ocrText.length > 0).length
};

// Préparation des données pour Vector Engine
return {
  json: {
    documentId,
    vectorEngineUrl: documentInfo.vectorEngineUrl,
    textBlocks,
    images: processedImages,
    metadata: {
      ...metadata,
      pageCount,
      imageStats: stats
    },
    fileName: documentInfo.fileName,
    fullPath: documentInfo.fullPath,
    timestamp: new Date().toISOString()
  }
};

// Préparation des statistiques et de la réponse finale
const vectorEngineResults = $json.body || {};

// Vérifier si le Vector Engine a bien fonctionné
if (!vectorEngineResults.success) {
  throw new Error(`Vector Engine failed: ${vectorEngineResults.message || 'Unknown error'}`);
}

// Récupérer les informations
const documentInfo = $input.item.json;
const documentId = documentInfo.documentId;
const fileName = documentInfo.fileName;
const metadata = documentInfo.metadata || {};
const imageStats = metadata.imageStats || {};

// Récupérer les statistiques du Vector Engine
const vectorStats = vectorEngineResults.stats || {};

return {
  json: {
    success: true,
    documentId,
    fileName,
    processingCompleted: true,
    timestamp: new Date().toISOString(),
    stats: {
      textBlocksCount: vectorStats.totalTextBlocks || documentInfo.textBlocks?.length || 0,
      chunksCreated: vectorStats.chunksCount || 0,
      vectorsIndexed: vectorStats.indexedCount || 0,
      imagesCount: imageStats.totalImages || documentInfo.images?.length || 0,
      technicalDiagramsCount: imageStats.technicalDiagrams || 0,
      pageCount: metadata.pageCount || 0
    },
    microservicesUsed: {
      documentProcessor: true,
      schemaAnalyzer: true,
      vectorEngine: true
    },
    status: 'indexed',
    retrievalReady: true,
    searchUrl: vectorEngineResults.searchEndpoint || null
  }
};

// Vérification de l'état des microservices

const microserviceUrls = {
  documentProcessor: 'http://technicia-document-processor:8000/health',
  schemaAnalyzer: 'http://technicia-vision-classifier:8000/health',
  vectorEngine: 'http://technicia-vector-store:8000/health'
};

// Fonction pour vérifier l'état d'un microservice
async function checkServiceHealth(url) {
  try {
    const response = await $http.get(url);
    return response.status === 200 ? 'operational' : 'degraded';
  } catch (error) {
    return 'unavailable';
  }
}

// Vérification de l'état de tous les services (en parallèle)
const documentProcessorStatus = await checkServiceHealth(microserviceUrls.documentProcessor);
const schemaAnalyzerStatus = await checkServiceHealth(microserviceUrls.schemaAnalyzer);
const vectorEngineStatus = await checkServiceHealth(microserviceUrls.vectorEngine);

// Déterminer l'état global du système
const allOperational = (
  documentProcessorStatus === 'operational' &&
  schemaAnalyzerStatus === 'operational' &&
  vectorEngineStatus === 'operational'
);

const anyUnavailable = (
  documentProcessorStatus === 'unavailable' ||
  schemaAnalyzerStatus === 'unavailable' ||
  vectorEngineStatus === 'unavailable'
);

const systemStatus = allOperational ? 'operational' : (anyUnavailable ? 'service_outage' : 'degraded');

return {
  json: {
    status: systemStatus,
    service: 'TechnicIA Document Ingestion',
    version: '2.0.0',
    timestamp: new Date().toISOString(),
    microservices: {
      documentProcessor: documentProcessorStatus,
      schemaAnalyzer: schemaAnalyzerStatus,
      vectorEngine: vectorEngineStatus
    }
  }
};
