// Récupération et validation du fichier PDF
const result = {};

try {
  // 1. Vérifier la présence de données binaires
  if (!$input.item.binary) {
    throw new Error('Aucune donnée binaire reçue');
  }

  // 2. Trouver la propriété contenant le fichier (doit être 'file' selon le frontend)
  let binaryPropertyName = null;
  const binaryProperties = Object.keys($input.item.binary);

  // Le frontend utilise la clé 'file' dans FormData.append('file', fileObj.file)
  if (binaryProperties.includes('file')) {
    binaryPropertyName = 'file';
  } else {
    // Fallback: utiliser la première propriété disponible
    binaryPropertyName = binaryProperties[0];
  }

  if (!binaryPropertyName) {
    throw new Error('Aucune propriété binaire trouvée');
  }

  // 3. Extraire les informations sur le fichier
  const fileData = $input.item.binary[binaryPropertyName];

  // 4. Validation du type de fichier
  if (fileData.mimeType !== 'application/pdf') {
    throw new Error('Le fichier doit être au format PDF');
  }

  // 5. Extraire les métadonnées
  const fileName = fileData.fileName || 'document.pdf';
  const fileSize = fileData.fileSize || 0;

  // 6. Logging des métadonnées
  console.log('Fichier reçu:', fileName);
  console.log('Taille:', fileSize, 'octets');
  console.log('Type MIME:', fileData.mimeType);
  console.log('Propriété binaire:', binaryPropertyName);

  // 7. Préparer le résultat
  result.fileName = fileName;
  result.fileSize = fileSize;
  result.mimeType = fileData.mimeType;
  result.binaryPropertyName = binaryPropertyName;
  result.uploadTime = new Date().toISOString();
  result.success = true;

  return result;

} catch (error) {
  console.error('Erreur lors du traitement du fichier:', error.message);
  return {
    success: false,
    error: error.message,
    timestamp: new Date().toISOString()
  };
}

// Simulation de traitement de document pour contourner les problèmes potentiels avec le microserveur
// Cela permet au frontend de fonctionner même si le microserveur Python n'est pas disponible

const inputData = $input.item.json;

// Créer une réponse simulée qui correspond exactement à ce que le frontend attend
const result = {
  success: true,
  document_id: 'doc-' + Date.now(), // ID unique basé sur le timestamp
  document_name: inputData.fileName || 'document.pdf',
  text_chunks_count: 8, // Valeur simulée
  images_count: 3, // Valeur simulée
  processing_type: 'simulated',
  processing_details: {
    processing_time: 1.5, // Temps simulé
    page_count: 12, // Nombre de pages simulé
    mime_type: inputData.mimeType || 'application/pdf'
  }
};

// Log pour le débogage
console.log('Réponse simulée pour contourner les problèmes de microserveur:', JSON.stringify(result, null, 2));

return result;

